search_sql = {
    "searchByProductName": "SELECT ca.name ,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE ca.id={category_id} and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%' ",
    "searchAll": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE  c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%' ",
    "searchByProductBrands": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE c.etp_id=(SELECT id from enterprise WHERE enterprise_name='{etp}') and c.com_tags LIKE '%{condition}%'  and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%'",
    "searchByProductBrand": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE c.etp_id=(SELECT id from enterprise WHERE enterprise_name='{etp}') and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%'",
    "searchByProductVarieties": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE c.category_id =(SELECT id from category WHERE name='{condition}') and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%'",
    "searchByProductTag": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE c.com_tags LIKE '%{condition}%'  and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%' ",
    "searchByProductTags": "SELECT ca.name,e.enterprise_name,c.* from commodity c LEFT JOIN category ca on c.category_id = ca.id LEFT JOIN enterprise e on c.etp_id=e.id WHERE c.etp_id=(SELECT id from enterprise WHERE enterprise_name='{etp}') and c.com_tags LIKE '%{condition}%'  and c.commodity_name like'%{keyword}%' or c.commodity_component LIKE '%{keyword}%' ",
    "hotSearch": "SELECT j.id,j.title,j.click,j.images,j.cots,j.fbs,j.user_id,'journal' as t_name from journal j UNION ALL SELECT d.id,d.words,d.click,d.dynamic_images,d.cots,d.fbs,d.user_id,'dynamic' as t_name from dynamic d UNION ALL SELECT c.id,c.title,c.click,c.img,c.cots,c.fbs,c.user_id ,'test' as t_name from commodity_test_main as c ORDER BY click DESC LIMIT 10",
    "hotDairy": "SELECT id,click,cots,fbs,images,user_id,title from journal ORDER BY (click+cots*2+fbs*4) DESC LIMIT 10",
    "hotCosmetics": "SELECT enterprise.enterprise_name,c.* from commodity as c LEFT JOIN enterprise on c.etp_id=enterprise.id ORDER BY (c.click+c.cots*2+c.fbs*4) DESC LIMIT 10",
    "getUserName":"SELECT user_nickname FROM `user` WHERE user_id={user_id}",
    "hotKeyword":"SELECT s.search_content from search as s GROUP BY s.search_content ORDER BY COUNT(*) DESC LIMIT 20",
    "recordKeyword":"INSERT into search VALUE (null,'{keyword}',{data})",
    "searchDynamic": "SELECT u.user_nickname as user_name,d.* from dynamic d LEFT JOIN `user` u on u.user_id=d.user_id WHERE words like '%{keyword}%' or tags like '%{keyword}%'",
    "searchJournal": "SELECT u.user_nickname as user_name,j.* from journal j LEFT JOIN `user` u on u.user_id=j.user_id WHERE title like '%{keyword}%' or tags like '%{keyword}%' or words LIKE '%{keyword}%'",
    "searchTest": "SELECT u.user_nickname as user_name,c.* from commodity_test_main c LEFT JOIN `user` u on u.user_id=c.user_id WHERE title like '%{keyword}%' or content like '%{keyword}%' or tags LIKE '%{keyword}%'",
    "searchCommodity": "SELECT enterprise.enterprise_name as etp_name,commodity.* from commodity LEFT JOIN enterprise on commodity.etp_id=enterprise.id  WHERE commodity.id ={id}",
    "commodityComment": "SELECT cc.*,u.* from (commodity_comment cc LEFT JOIN commodity c on cc.commodity_id=c.id) LEFT JOIN `user` u on cc.user_id=u.user_id  WHERE c.id={id}"
}
